import React from "react";
import ReactDOM from "react-dom";

class FAQ extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: this.props.name,
      detail: this.props.detail,
      displayState: null,
      answer: [
        "This is a testing paragraph",
        "This is a testing paragraph",
        "This is a testing paragraph",
        "This is a testing paragraph",
        "This is a testing paragraph",
      ],
    };
  }

  handleClick = (index) => {
    let { displayState, answer } = this.state;
    let increase;
    if (displayState == null) increase = answer[index];
    else increase = null;
    this.setState({ displayState: increase });
  };
  render() {
    const { name, detail, displayState } = this.state;
    return (
      <div
        className="col-12 border-bottom border-dark pt-3 "
        data-aos="fade-down"
        data-aos-duration="1000"
      >
        <div className="row justify-content-between">
          <div className="lead font-weight-bold text-capitalize text-muted">
            {name}
          </div>
          <div
            onClick={this.handleClick}
            className="h2 text-muted float-right"
            type="button"
          >
            {" "}
            +{" "}
          </div>
        </div>
        <div className={this.state.displayState + "text-left lead"}>
          {displayState}
        </div>
      </div>
    );
  }
}

export default FAQ;
