import React from "react";
import ReactDOM from "react-dom";
import "bootstrap/dist/css/bootstrap.css";
import "../FA Icons/css/all.min.css";
class NavBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      menu: ["Home", "About Us", "Services", "Testimonials", "Frequenly Asked"],
      hook: this.props.sections,
      sections: [
        "about_us",
        "services",
        "client_comment",
        "call_to_action",
        "frequently_asked",
        "footer",
      ],
    };
  }

  render() {
    const { menu, hook } = this.state;
    return (
      <div className="container">
        <div className="row">
          <div className="w-100">
            <nav className="navbar navbar-expand-lg justify-content-between ">
              <a className=" display-4 font-weight-light text-decoration-none text-dark">
                {" "}
                rapid{" "}
              </a>
              <button
                class="navbar-toggler"
                type="button"
                data-toggle="collapse"
                data-target="#navbarSupportedContent"
                aria-controls="navbarSupportedContent"
                aria-expanded="false"
                aria-label="Toggle navigation"
              >
                <i class="fas fa-bars " style={{ fontSize: "30px" }}></i>
              </button>

              <div className="navbar-collapse collapse justify-content-end">
                <ul className="nav float-right">
                  {menu.map((item, i) => {
                    return (
                      <li className="nav-item">
                        <a
                          className="nav-link text-uppercase "
                          href={hook[i].id}
                        >
                          home
                        </a>
                      </li>
                    );
                  })}
                </ul>
              </div>
            </nav>
          </div>
        </div>
      </div>
    );
  }
}

export default NavBar;
