import React from "react";
import ReactDOM from "react-dom";

class SectionHeader extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      headerName: this.props.headerName,
      headerDescribe: "This is just a testing message",
    };
  }

  checkOutState = () => {
    if (this.props.headerDescribe != "")
      this.setState({ headerDescribe: this.props.headerDescribe });
  };

  render() {
    return (
      <div className="container mt-2 p-5 ">
        <p className="h1 text-center font-weight-bold text-muted">
          {this.state.headerName}
        </p>
        {this.checkOutState()}
        <p className="text-muted lead text-center ">
          {" "}
          {this.state.headerDescribe}
        </p>
      </div>
    );
  }
}

export default SectionHeader;
