import React from "react";
import ReactDOM from "react-dom";

class FAQ extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: this.props.name,
      detail: this.props.detail,
      displayState: " d-none ",
    };
  }

  handleClick = () => {
    if (this.state.displayState == "d-none")
      this.setState({ displayState: " d-block" });
    else this.setState({ displayState: " d-none" });
  };
  render() {
    const { name, detail } = this.state;
    return (
      <div className="col-12 border-bottom border-dark pt-3 ">
        <div className="row justify-content-between">
          <div className="lead font-weight-bold text-capitalize text-muted">
            {name}
          </div>
          <div
            onClick={() => this.handleClick()}
            className="h2 text-muted float-right"
            type="button"
          >
            {" "}
            +{" "}
          </div>
          <div className={this.state.displayState + "text-left lead"}>
            {this.state.detail}
          </div>
        </div>
      </div>
    );
  }
}

export default FAQ;
