import React from "react";
import ReactDOM from "react-dom";
import SectionHeader from "./SectionHeader";
import Client from "./Client";
import cli1 from "../img/testimonial-2.jpg";
import cli2 from "../img/testimonial-2.jpg";
import cli3 from "../img/testimonial-2.jpg";
import cli4 from "../img/testimonial-2.jpg";
class ClientComment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      imgSrc: [cli1, cli2, cli3, cli4],
      cliName: [
        "Timon Sithatra",
        "Tom Michaelson",
        "Tinker Hatfield",
        "Michael Jordan",
      ],
      cliCmt: ["", "", "", ""],
    };
  }

  render() {
    const { imgSrc, cliName, cliCmt } = this.state;
    return (
      <div className="container mb-5 pb-5">
        <SectionHeader headerName="Testimonials" />

        {}

        <Client
          imgSrc={cli1}
          cliName="Timon Sithattra"
          cliCmt="this si justt a paragraph that i use to contain something, it doesnt have muc meaning "
        />
      </div>
    );
  }
}

export default ClientComment;
